mapper.insert:
INSERT INTO `user`
SET nickname = #{nickname}
    , password = #{password}
    , email = #{email}
    , profile_pic = #{pic}
    , first_login = TRUE

mapper.select:
SELECT EXISTS (
    SELECT email FROM `user`
    WHERE email=#{email}
)

SELECT EXISTS (
    SELECT nickname FROM `user`
    WHERE nickname=#{nickname}
)

mapper.update:
UPDATE `user`
SET nickname = #{nickname}
    , password = #{password}
    , profile_pic = #{pic}
    , status_message = #{statusMessage}
WHERE user_no = #{targetUserNo}
